mapper.select:
select
	count('x') 
from DB_ALLPET.tb_board
where  	board_type = #{boardType}
and		board_ssid = #{boardSsid}
and		deleteYn = 'N'

mapper.update:
update DB_ALLPET.tb_board
	set title = #{title}

		, content = #{content}


		, username = #{username}


		, passwd = #{passwd}


		, email = #{email}

where  	board_Seq = #{boardSeq}

update DB_ALLPET.tb_board
	set rcount = rcount + 1
where  	board_Seq = #{boardSeq}

update DB_ALLPET.tb_board
	set deleteYn = 'Y'
where  	board_Seq = #{boardSeq}
