mapper.select:
SELECT s_id, s_group, s_lcode, s_mcode, s_scode, s_type, CASE WHEN S_TYPE = 'SVAL' THEN S_SVAL WHEN S_TYPE = 'LVAL' THEN S_LVAL ELSE S_STATUS END AS s_val, s_status, CASE WHEN N_LV = 1 THEN S_GROUP WHEN N_LV = 2 THEN S_LCODE WHEN N_LV = 3 THEN S_MCODE WHEN N_LV = 4 THEN S_SCODE ELSE '' END AS s_name FROM DB_ALLPET.T_HOSPITAL_STATUS_INFO WHERE S_ID = #{ssid}
AND S_GROUP = #{group}
AND S_LCODE = #{lcode}
AND S_MCODE = #{mcode}
AND S_SCODE = #{scode}
